let hamburger=document.querySelector(".hamburger"),navbar=document.querySelector(".navbar"),sections=document.querySelectorAll("section"),navLinks=document.querySelectorAll("header nav a");window.onscroll=()=>{sections.forEach((e=>{window.scrollY>=e.offsetTop-150&&navLinks.forEach((t=>{t.classList.remove("active"),e.getAttribute("id")===t.getAttribute("href").substring(1)&&t.classList.add("active")}))}))},hamburger.onclick=()=>{hamburger.classList.toggle("active"),navbar.classList.toggle("active")},navLinks.forEach((e=>{e.addEventListener("click",(()=>{navbar.classList.remove("active"),hamburger.classList.remove("active")}))}));